import React, { useState, useEffect } from 'react'
import { View , Text,FlatList} from 'react-native'
import firebase from 'firebase';
import { connect } from 'react-redux';
import { fetchUserPosts } from '../../redux/actions';

function Follow() {
    const [posts, setPosts] = React.useState([]);
    const [falove, setFalove] = React.useState([])
    const uid = firebase.auth().currentUser.uid







    React.useEffect(() => {
       firebase.firestore() 
        .collection('following')
        .where('uid', '==', uid)
        .onSnapshot(querySnapshot => {
            const falove = [];
            querySnapshot.forEach(documentSnapshot =>
                falove.push({
                    ...documentSnapshot.data(),
                    key:documentSnapshot.id,
                })
                )
                setFalove(falove)
                console.log(falove.length)
                firebase.firestore()
                .collection('posts')
                .onSnapshot(querySnapshot => {
                  const posts = [];
            
                  querySnapshot.forEach(documentSnapshot => {
                    console.log(falove.length)
                    console.log(documentSnapshot.id)
                      if(checkFalove(documentSnapshot.id)){
                      posts.push({
                      ...documentSnapshot.data(),
                      key: documentSnapshot.id,
                      })
                    };
                  });
            
                  setPosts(posts);
                })
            }
            )
        

        }, []);
    
        const checkFalove = (id) => {
            console.log(checkFalove.length)
            for (var i = 0; i < falove.length; i++){
                if(id == falove[i].followed){
                    return true;
                }
            }
            return false;
            
        }

      return (
        <FlatList
          data={posts}
          renderItem={({ item }) => (
            <View style={{flex: 1, alignItems: 'center', justifyContent: 'center' }}>
              <Text>title: {item.title}</Text>
              <Text>topic: {item.topic}</Text>
              <Text>description: {item.description}</Text>
              <Text>username: {item.username}</Text>
              <Text
              onPress = {()=>
               props.navigation.navigate('Comment',
              {postId: item.id})}>
              See Comments
            </Text>
            </View>
           
          )}
        />
      )
}

const mapStateToProps = (store) => ({
  currentUser: store.userState.currentUser,
  posts: store.userState.posts,
  following: store.userState.following,
})

export default connect(mapStateToProps, null)(Follow);
