import React from 'react'
import { StyleSheet, View , Text,useState,useEffect, FlatList} from 'react-native'
import {fetchUser} from '../../redux/actions/index'
import firebase from 'firebase';


export default function Profile(navigation) {
    const [loading, setLoading] = React.useState(true); // Set loading to true on component mount
    const [posts, setPosts] = React.useState([]); // Initial empty array of users
    
    React.useEffect(() => {
      const uid = firebase.auth().currentUser.uid
        const subscriber = firebase.firestore()
          .collection('posts')
          .where('uid', '==', uid)
          .onSnapshot(querySnapshot => {
            const posts = [];
      
            querySnapshot.forEach(documentSnapshot => {
                posts.push({
                ...documentSnapshot.data(),
                key: documentSnapshot.id,
              });
            });
      
            setPosts(posts);
            setLoading(false);
            console.log()
          });
      
        // Unsubscribe from events when no longer in use
        return () => subscriber();
      }, []);
    
    
      return (
        <View style={styles.container}>
        <View style={styles.containerInfo}>
            <Text>{user.name}</Text>
            <Text>{user.email}</Text>

            {props.route.params.uid !== firebase.auth().currentUser.uid ? (
                <View>
                    {following ? (
                        <Button
                            title="Following"
                            onPress={() => onUnfollow()}
                        />
                    ) :
                        (
                            <Button
                                title="Follow"
                                onPress={() => onFollow()}
                            />
                        )}
                </View>
            ) :
                <Button
                    title="Logout"
                    onPress={() => onLogout()}
                />}
        </View>
        <View style={styles.containerGallery}>
        <FlatList
          data={posts}
          renderItem={({ item }) => (
            <View style={{ height: 50, flex: 1, alignItems: 'center', justifyContent: 'center' }}>
              <Text>Title: {item.title}</Text>
              <Text>Topic: {item.topic}</Text>
            </View>
          )}
        />
        </View>
        </View>
      )
}

const styles = StyleSheet.create({
  container: {
      flex: 1,
  },
  containerInfo: {
      margin: 20
  },
  containerGallery: {
      flex: 1
  },
  containerImage: {
      flex: 1 / 3

  },
  image: {
      flex: 1,
      aspectRatio: 1 / 1
  }
})
